scripts/create-super-admin.ts.content:
import { config } from "dotenv";
import { drizzle } from "drizzle-orm/node-postgres";
import { Pool } from "pg";
import { users } from "@shared/schema";
import { eq } from "drizzle-orm";
import bcrypt from "bcrypt";

// Load environment variables
config();

async function createSuperAdmin() {
  const pool = new Pool({
    connectionString: process.env.DATABASE_URL,
  });

  const db = drizzle(pool);

  try {
    // Check if super admin already exists
    const existingSuperAdmin = await db.select().from(users).where(eq(users.role, "super_admin"));

    if (existingSuperAdmin.length > 0) {
      console.log("Super admin already exists:");
      existingSuperAdmin.forEach(admin => {
        console.log(`- ${admin.username} (${admin.email})`);
      });
      return;
    }

    // Create super admin user
    const superAdminData = {
      username: "superadmin",
      email: "superadmin@diyormarket.com",
      password: await bcrypt.hash("SuperAdmin123!", 10),
      firstName: "Super",
      lastName: "Admin",
      role: "super_admin" as const,
      permissions: [
        "manage_products",
        "manage_users", 
        "manage_orders",
        "view_analytics",
        "manage_settings",
        "view_logs",
        "manage_admins",
        "system_maintenance"
      ],
      isActive: true
    };

    const result = await db.insert(users).values(superAdminData).returning();

    console.log("‚úÖ Super Admin created successfully!");
    console.log(`Username: ${result[0].username}`);
    console.log(`Email: ${result[0].email}`);
    console.log(`Password: SuperAdmin123!`);
    console.log("\nüîê Please change the password after first login!");
    console.log("\nüåê Access Super Admin Dashboard at: http://localhost:3000/super-admin");

  } catch (error) {
    console.error("‚ùå Error creating super admin:", error);
  } finally {
    await pool.end();
  }
}

// Run the script
if (import.meta.url === `file://${process.argv[1]}`) {
  createSuperAdmin();
}

scripts/seed-data.ts.content:
import { config } from "dotenv";
import { db } from "../server/storage";
import { categories, products } from "../shared/schema";
import { categories as mockCategories } from "../client/src/data/categories";
import { products as mockProducts } from "../client/src/data/products";

// Load environment variables
config();

async function seedCategories() {
  console.log("Seeding categories...");

  for (const category of mockCategories) {
    try {
      await db.insert(categories).values({
        name: category.name,
        nameEs: category.nameEs,
        nameAr: category.nameAr,
        image: category.image,
        slug: category.slug,
        isActive: true,
      });
      console.log(`‚úì Added category: ${category.name}`);
    } catch (error) {
      console.log(`‚ö† Category ${category.name} might already exist`);
    }
  }
}

async function seedProducts() {
  console.log("Seeding products...");

  // First, get all categories to map them to products
  const dbCategories = await db.select().from(categories);
  const categoryMap = new Map();
  dbCategories.forEach(cat => {
    categoryMap.set(cat.name, cat.id);
  });

  for (const product of mockProducts) {
    try {
      const categoryId = categoryMap.get(product.category.name);

      if (!categoryId) {
        console.log(`‚ö† Skipping product ${product.name} - category not found`);
        continue;
      }

      await db.insert(products).values({
        name: product.name,
        nameEs: product.nameEs,
        nameAr: product.nameAr,
        description: product.description,
        descriptionEs: product.descriptionEs,
        descriptionAr: product.descriptionAr,
        price: product.price.toString(),
        image: product.image,
        categoryId: categoryId,
        unit: product.unit,
        unitEs: product.unitEs,
        unitAr: product.unitAr,
        inStock: product.inStock,
        featured: product.featured || false,
        sale: product.sale || false,
        salePrice: product.salePrice?.toString(),
        nutrition: product.nutrition,
        allergens: product.allergens,
      });
      console.log(`‚úì Added product: ${product.name}`);
    } catch (error) {
      console.log(`‚ö† Product ${product.name} might already exist`);
    }
  }
}

async function seedDatabase() {
  try {
    console.log("Starting database seeding...");

    await seedCategories();
    await seedProducts();

    console.log("‚úÖ Database seeding completed successfully!");
  } catch (error) {
    console.error("‚ùå Database seeding failed:", error);
    process.exit(1);
  }
}

// Run the seeding if this file is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  seedDatabase();
}

export { seedDatabase };

