#!/usr/bin/env tsx

import { db } from "../server/storage";
import { seedDatabase } from "./seed-data";

async function setupDatabase() {
  try {
    console.log("üöÄ Setting up FreshCart database...");

    // Test database connection
    console.log("üì° Testing database connection...");
    await db.execute("SELECT 1");
    console.log("‚úÖ Database connection successful!");

    // Run database migrations (this will be handled by drizzle-kit)
    console.log("üìã Running database migrations...");
    console.log("‚ö†Ô∏è  Please run 'npm run db:push' to apply schema changes");

    // Seed initial data
    console.log("üå± Seeding initial data...");
    await seedDatabase();

    console.log("üéâ Database setup completed successfully!");
    console.log("\nNext steps:");
    console.log("1. Run 'npm run db:push' to apply schema changes");
    console.log("2. Start the development server with 'npm run dev'");
    console.log("3. Test the API endpoints");

  } catch (error) {
    console.error("‚ùå Database setup failed:", error);
    console.log("\nTroubleshooting:");
    console.log("1. Make sure DATABASE_URL is set in your environment");
    console.log("2. Ensure your database is running and accessible");
    console.log("3. Check your database credentials");
    process.exit(1);
  }
}

setupDatabase();

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import cors from "cors";
import session from "express-session";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.set("trust proxy", 1);
// CORS (safe default: allow same-origin in production; allow configured origins otherwise)
const allowedOrigins = (process.env.CORS_ORIGIN || "").split(",").map(s => s.trim()).filter(Boolean);
app.use(cors({
  origin: (_origin, callback) => {
    if (app.get("env") === "production" && allowedOrigins.length === 0) {
      // In production, if no CORS_ORIGIN provided we assume same-origin and deny cross-site
      return callback(null, false);
    }
    if (!allowedOrigins.length || (typeof _origin === "string" && allowedOrigins.includes(_origin))) {
      return callback(null, true);
    }
    return callback(null, false);
  },
  credentials: true,
}));
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
app.use(session({
  secret: process.env.SESSION_SECRET || "your-secret-key-change-in-production",
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: process.env.NODE_ENV === "production",
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000, // 24 hours
  },
}));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Serve the app on port 5000 for Replit environment
  // this serves both the API and the client.
  const port = process.env.PORT || 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import { storage, verifyPassword } from "./storage";
import { insertUserSchema, insertAddressSchema } from "@shared/schema";
import { ZodError } from "zod";

// Extend the session interface to include our custom properties
declare module "express-session" {
  interface SessionData {
    userId?: number;
    username?: string;
  }
}

// Middleware to check if user is authenticated
const authenticateUser = (req: Request, res: Response, next: any) => {
  if (req.session?.userId) {
    next();
  } else {
    res.status(401).json({ error: "Unauthorized" });
  }
};

// Middleware to check if user has required role
const requireRole = (roles: string[]) => {
  return async (req: Request, res: Response, next: any) => {
    try {
      // Check if user is authenticated first
      if (!req.session?.userId) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      const user = await storage.getUser(req.session.userId);
      if (!user) {
        return res.status(401).json({ error: "Unauthorized" });
      }

      if (!roles.includes(user.role)) {
        return res.status(403).json({ error: "Forbidden: Insufficient permissions" });
      }

      next();
    } catch (error) {
      console.error("Role check error:", error);
      res.status(500).json({ error: "Authorization check failed" });
    }
  };
};

// Middleware to validate request body with Zod schema
const validateBody = (schema: any) => {
  return (req: Request, res: Response, next: any) => {
    try {
      req.body = schema.parse(req.body);
      next();
    } catch (error) {
      if (error instanceof ZodError) {
        res.status(400).json({ 
          error: "Validation error", 
          details: error.errors 
        });
      } else {
        res.status(500).json({ error: "Internal server error" });
      }
    }
  };
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication routes
  app.post("/api/auth/register", validateBody(insertUserSchema), async (req: Request, res: Response) => {
    try {
      const { username, email, password, firstName, lastName, phone } = req.body;

      // Check if user already exists
      const existingUser = await storage.getUserByUsername(username);
      if (existingUser) {
        return res.status(400).json({ error: "Username already exists" });
      }

      const existingEmail = await storage.getUserByEmail(email);
      if (existingEmail) {
        return res.status(400).json({ error: "Email already exists" });
      }

      // Create new user
      const user = await storage.createUser({
        username,
        email,
        password,
        firstName,
        lastName,
        phone,
      });

      // Set session
      req.session.userId = user.id;
      req.session.username = user.username;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.status(201).json({ user: userWithoutPassword });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(500).json({ error: "Registration failed" });
    }
  });

  app.post("/api/auth/login", async (req: Request, res: Response) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ error: "Username and password are required" });
      }

      // Find user by username or email
      let user = await storage.getUserByUsername(username);
      if (!user) {
        user = await storage.getUserByEmail(username);
      }

      if (!user) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Verify password
      const isValidPassword = await verifyPassword(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      // Set session
      req.session.userId = user.id;
      req.session.username = user.username;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/auth/logout", (req: Request, res: Response) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ error: "Logout failed" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/me", authenticateUser, async (req: Request, res: Response) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ error: "Failed to get user" });
    }
  });

  // User profile routes
  app.get("/api/user/profile", authenticateUser, async (req: Request, res: Response) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error) {
      console.error("Get profile error:", error);
      res.status(500).json({ error: "Failed to get profile" });
    }
  });

  app.put("/api/user/profile", authenticateUser, async (req: Request, res: Response) => {
    try {
      const { firstName, lastName, phone } = req.body;

      const updatedUser = await storage.updateUser(req.session.userId!, {
        firstName,
        lastName,
        phone,
      });

      if (!updatedUser) {
        return res.status(404).json({ error: "User not found" });
      }

      const { password: _, ...userWithoutPassword } = updatedUser;
      res.json({ user: userWithoutPassword });
    } catch (error) {
      console.error("Update profile error:", error);
      res.status(500).json({ error: "Failed to update profile" });
    }
  });

  // Address routes
  app.get("/api/user/addresses", authenticateUser, async (req: Request, res: Response) => {
    try {
      const addresses = await storage.getUserAddresses(req.session.userId!);
      res.json({ addresses });
    } catch (error) {
      console.error("Get addresses error:", error);
      res.status(500).json({ error: "Failed to get addresses" });
    }
  });

  app.post("/api/user/addresses", authenticateUser, validateBody(insertAddressSchema), async (req: Request, res: Response) => {
    try {
      const address = await storage.createAddress({
        ...req.body,
        userId: req.session.userId!,
      });
      res.status(201).json({ address });
    } catch (error) {
      console.error("Create address error:", error);
      res.status(500).json({ error: "Failed to create address" });
    }
  });

  app.put("/api/user/addresses/:id", authenticateUser, async (req: Request, res: Response) => {
    try {
      const addressId = parseInt(req.params.id);
      const updatedAddress = await storage.updateAddress(addressId, req.body);

      if (!updatedAddress) {
        return res.status(404).json({ error: "Address not found" });
      }

      res.json({ address: updatedAddress });
    } catch (error) {
      console.error("Update address error:", error);
      res.status(500).json({ error: "Failed to update address" });
    }
  });

  app.delete("/api/user/addresses/:id", authenticateUser, async (req: Request, res: Response) => {
    try {
      const addressId = parseInt(req.params.id);
      const success = await storage.deleteAddress(addressId);

      if (!success) {
        return res.status(404).json({ error: "Address not found" });
      }

      res.json({ message: "Address deleted successfully" });
    } catch (error) {
      console.error("Delete address error:", error);
      res.status(500).json({ error: "Failed to delete address" });
    }
  });

  // Product routes
  app.get("/api/products", async (req: Request, res: Response) => {
    try {
      const products = await storage.getProducts();
      res.json({ products });
    } catch (error) {
      console.error("Get products error:", error);
      res.status(500).json({ error: "Failed to get products" });
    }
  });

  app.get("/api/products/featured", async (req: Request, res: Response) => {
    try {
      const products = await storage.getFeaturedProducts();
      res.json({ products });
    } catch (error) {
      console.error("Get featured products error:", error);
      res.status(500).json({ error: "Failed to get featured products" });
    }
  });

  app.get("/api/products/:id", async (req: Request, res: Response) => {
    try {
      const productId = parseInt(req.params.id);
      const product = await storage.getProduct(productId);

      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }

      res.json({ product });
    } catch (error) {
      console.error("Get product error:", error);
      res.status(500).json({ error: "Failed to get product" });
    }
  });

  app.get("/api/categories", async (req: Request, res: Response) => {
    try {
      const categories = await storage.getCategories();
      res.json({ categories });
    } catch (error) {
      console.error("Get categories error:", error);
      res.status(500).json({ error: "Failed to get categories" });
    }
  });

  // Product Management routes (Admin/Product Manager)
  app.post("/api/admin/products", authenticateUser, requireRole(["super_admin", "admin", "product_manager"]), async (req: Request, res: Response) => {
    try {
      const { name, description, price, salePrice, categoryId, stockQuantity, featured, sale, image, unit } = req.body;

      const product = await storage.createProduct({
        name,
        description,
        price: parseFloat(price),
        salePrice: salePrice ? parseFloat(salePrice) : null,
        categoryId: parseInt(categoryId),
        stockQuantity: parseInt(stockQuantity),
        featured: Boolean(featured),
        sale: Boolean(sale),
        image,
        unit: unit || "—à—Ç" // Default unit if not provided
      });

      res.status(201).json({ product });
    } catch (error) {
      console.error("Create product error:", error);
      res.status(500).json({ error: "Failed to create product" });
    }
  });

  app.put("/api/admin/products/:id", authenticateUser, requireRole(["super_admin", "admin", "product_manager"]), async (req: Request, res: Response) => {
    try {
      const productId = parseInt(req.params.id);
      const { name, description, price, salePrice, categoryId, stockQuantity, featured, sale, image, unit } = req.body;

      const product = await storage.updateProduct(productId, {
        name,
        description,
        price: parseFloat(price),
        salePrice: salePrice ? parseFloat(salePrice) : null,
        categoryId: parseInt(categoryId),
        stockQuantity: parseInt(stockQuantity),
        featured: Boolean(featured),
        sale: Boolean(sale),
        image,
        unit: unit || "—à—Ç" // Default unit if not provided
      });

      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }

      res.json({ product });
    } catch (error) {
      console.error("Update product error:", error);
      res.status(500).json({ error: "Failed to update product" });
    }
  });

  app.delete("/api/admin/products/:id", authenticateUser, requireRole(["super_admin", "admin", "product_manager"]), async (req: Request, res: Response) => {
    try {
      const productId = parseInt(req.params.id);
      const success = await storage.deleteProduct(productId);

      if (!success) {
        return res.status(404).json({ error: "Product not found" });
      }

      res.json({ message: "Product deleted successfully" });
    } catch (error) {
      console.error("Delete product error:", error);
      res.status(500).json({ error: "Failed to delete product" });
    }
  });

  // Order routes
  app.get("/api/orders", authenticateUser, async (req: Request, res: Response) => {
    try {
      const orders = await storage.getUserOrders(req.session.userId!);
      res.json({ orders });
    } catch (error) {
      console.error("Get orders error:", error);
      res.status(500).json({ error: "Failed to get orders" });
    }
  });

  app.get("/api/orders/:id", authenticateUser, async (req: Request, res: Response) => {
    try {
      const orderId = parseInt(req.params.id);
      const order = await storage.getOrder(orderId);

      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      // Check if user owns this order
      if (order.userId !== req.session.userId!) {
        return res.status(403).json({ error: "Access denied" });
      }

      res.json({ order });
    } catch (error) {
      console.error("Get order error:", error);
      res.status(500).json({ error: "Failed to get order" });
    }
  });

  app.post("/api/orders", authenticateUser, async (req: Request, res: Response) => {
    try {
      const { items, addressId, paymentMethod } = req.body;

      if (!items || items.length === 0) {
        return res.status(400).json({ error: "Order must contain at least one item" });
      }

      if (!addressId || !paymentMethod) {
        return res.status(400).json({ error: "Address and payment method are required" });
      }

      // Validate that the address belongs to the user (security)
      const address = await storage.getAddress(addressId);
      if (!address || address.userId !== req.session.userId) {
        return res.status(403).json({ error: "Invalid address" });
      }

      // Fetch actual product prices from database (security: never trust client prices)
      let subtotal = 0;
      const validatedItems = [];

      for (const item of items) {
        const product = await storage.getProduct(item.productId);

        if (!product) {
          return res.status(400).json({ error: `Product ${item.productId} not found` });
        }

        if (!product.inStock || product.stockQuantity < item.quantity) {
          return res.status(400).json({ error: `Product ${product.name} is out of stock` });
        }

        // Use database prices, not client prices
        const price = product.sale && product.salePrice 
          ? parseFloat(product.salePrice) 
          : parseFloat(product.price);

        subtotal += price * item.quantity;
        validatedItems.push({
          productId: item.productId,
          quantity: item.quantity,
          price: price,
        });
      }

      const deliveryFee = 37500; // 37,500 UZS delivery fee
      const tax = subtotal * 0.08; // 8% tax
      const total = subtotal + deliveryFee + tax;

      const order = await storage.createOrder({
        userId: req.session.userId!,
        status: "pending",
        total: total.toString(),
        subtotal: subtotal.toString(),
        tax: tax.toString(),
        deliveryFee: deliveryFee.toString(),
        discount: "0",
        addressId,
        paymentMethod,
        paymentStatus: "pending",
      });

      // Decrement stock quantity for each validated item
      for (const item of validatedItems) {
        await storage.decrementProductStock(item.productId, item.quantity);
      }

      res.status(201).json({ order });
    } catch (error) {
      console.error("Create order error:", error);
      res.status(500).json({ error: "Failed to create order" });
    }
  });

  // Super Admin Routes
  app.get("/api/super-admin/users", requireRole(["super_admin"]), async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Get users error:", error);
      res.status(500).json({ error: "Failed to get users" });
    }
  });

  app.get("/api/super-admin/users/:id", requireRole(["super_admin"]), async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const user = await storage.getUserById(id);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ error: "Failed to get user" });
    }
  });

  app.post("/api/super-admin/users", requireRole(["super_admin"]), async (req, res) => {
    try {
      const { username, email, password, firstName, lastName, role, permissions } = req.body;

      if (!username || !email || !password || !role) {
        return res.status(400).json({ error: "Missing required fields" });
      }

      const user = await storage.createAdminUser({
        username,
        email,
        password,
        firstName,
        lastName,
        role,
        permissions
      });

      // Log the action
      await storage.logSystemAction({
        userId: req.session?.userId,
        action: "create_admin_user",
        resource: "users",
        resourceId: user.id,
        details: { role, permissions },
        ipAddress: req.ip,
        userAgent: req.get("User-Agent")
      });

      res.status(201).json(user);
    } catch (error) {
      console.error("Create admin user error:", error);
      res.status(500).json({ error: "Failed to create admin user" });
    }
  });

  app.put("/api/super-admin/users/:id/role", requireRole(["super_admin"]), async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const { role, permissions } = req.body;

      const user = await storage.updateUserRole(id, role, permissions);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      // Log the action
      await storage.logSystemAction({
        userId: req.session?.userId,
        action: "update_user_role",
        resource: "users",
        resourceId: id,
        details: { role, permissions },
        ipAddress: req.ip,
        userAgent: req.get("User-Agent")
      });

      res.json(user);
    } catch (error) {
      console.error("Update user role error:", error);
      res.status(500).json({ error: "Failed to update user role" });
    }
  });

  app.put("/api/super-admin/users/:id/activate", requireRole(["super_admin"]), async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.activateUser(id);
      if (!success) {
        return res.status(404).json({ error: "User not found" });
      }

      // Log the action
      await storage.logSystemAction({
        userId: req.session?.userId,
        action: "activate_user",
        resource: "users",
        resourceId: id,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent")
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Activate user error:", error);
      res.status(500).json({ error: "Failed to activate user" });
    }
  });

  app.put("/api/super-admin/users/:id/deactivate", requireRole(["super_admin"]), async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deactivateUser(id);
      if (!success) {
        return res.status(404).json({ error: "User not found" });
      }

      // Log the action
      await storage.logSystemAction({
        userId: req.session?.userId,
        action: "deactivate_user",
        resource: "users",
        resourceId: id,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent")
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Deactivate user error:", error);
      res.status(500).json({ error: "Failed to deactivate user" });
    }
  });

  app.delete("/api/super-admin/users/:id", requireRole(["super_admin"]), async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const success = await storage.deleteUser(id);
      if (!success) {
        return res.status(404).json({ error: "User not found" });
      }

      // Log the action
      await storage.logSystemAction({
        userId: req.session?.userId,
        action: "delete_user",
        resource: "users",
        resourceId: id,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent")
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Delete user error:", error);
      res.status(500).json({ error: "Failed to delete user" });
    }
  });

  app.get("/api/super-admin/logs", requireRole(["super_admin"]), async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 100;
      const offset = parseInt(req.query.offset as string) || 0;
      const logs = await storage.getSystemLogs(limit, offset);
      res.json(logs);
    } catch (error) {
      console.error("Get system logs error:", error);
      res.status(500).json({ error: "Failed to get system logs" });
    }
  });

  app.get("/api/super-admin/stats", requireRole(["super_admin"]), async (req, res) => {
    try {
      const stats = await storage.getSystemStats();
      res.json(stats);
    } catch (error) {
      console.error("Get system stats error:", error);
      res.status(500).json({ error: "Failed to get system stats" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { config } from "dotenv";
import { drizzle } from "drizzle-orm/node-postgres";
import { Pool } from "pg";
import { 
  users, 
  addresses, 
  categories, 
  products, 
  orders, 
  orderItems,
  systemLogs,
  type User, 
  type InsertUser,
  type Address,
  type InsertAddress,
  type Category,
  type InsertCategory,
  type Product,
  type InsertProduct,
  type Order,
  type InsertOrder,
  type OrderItem,
  type InsertOrderItem,
  type SystemLog,
  type UserRole,
  type Permission
} from "@shared/schema";
import { eq, and, sql } from "drizzle-orm";
import bcrypt from "bcrypt";

// Load environment variables
config();

// Database connection
const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle(pool);

// Storage interface
export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, user: Partial<InsertUser>): Promise<User | undefined>;
  deleteUser(id: number): Promise<boolean>;

  // Address operations
  getUserAddresses(userId: number): Promise<Address[]>;
  getAddress(id: number): Promise<Address | undefined>;
  createAddress(address: InsertAddress & { userId: number }): Promise<Address>;
  updateAddress(id: number, address: Partial<InsertAddress>): Promise<Address | undefined>;
  deleteAddress(id: number): Promise<boolean>;

  // Category operations
  getCategories(): Promise<Category[]>;
  getCategory(id: number): Promise<Category | undefined>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Product operations
  getProducts(): Promise<Product[]>;
  getProduct(id: number): Promise<Product | undefined>;
  getProductsByCategory(categoryId: number): Promise<Product[]>;
  getFeaturedProducts(): Promise<Product[]>;
  createProduct(product: {
    name: string;
    description: string;
    price: number;
    salePrice?: number | null;
    categoryId: number;
    stockQuantity: number;
    featured: boolean;
    sale: boolean;
    image: string;
    unit: string;
  }): Promise<Product>;

  // Order operations
  getUserOrders(userId: number): Promise<Order[]>;
  getOrder(id: number): Promise<Order | undefined>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrderStatus(id: number, status: string): Promise<Order | undefined>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.email, email)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    // Hash password before storing
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);

    const result = await db.insert(users).values({
      ...insertUser,
      password: hashedPassword,
    }).returning();

    return result[0];
  }

  async updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined> {
    // Hash password if it's being updated
    if (userData.password) {
      userData.password = await bcrypt.hash(userData.password, 10);
    }

    const result = await db.update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();

    return result[0];
  }


  // Address operations
  async getUserAddresses(userId: number): Promise<Address[]> {
    return await db.select().from(addresses).where(eq(addresses.userId, userId));
  }

  async getAddress(id: number): Promise<Address | undefined> {
    const result = await db.select().from(addresses).where(eq(addresses.id, id)).limit(1);
    return result[0];
  }

  async createAddress(addressData: InsertAddress & { userId: number }): Promise<Address> {
    const result = await db.insert(addresses).values(addressData).returning();
    return result[0];
  }

  async updateAddress(id: number, addressData: Partial<InsertAddress>): Promise<Address | undefined> {
    const result = await db.update(addresses)
      .set({ ...addressData, updatedAt: new Date() })
      .where(eq(addresses.id, id))
      .returning();

    return result[0];
  }

  async deleteAddress(id: number): Promise<boolean> {
    const result = await db.delete(addresses).where(eq(addresses.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories).where(eq(categories.isActive, true));
  }

  async getCategory(id: number): Promise<Category | undefined> {
    const result = await db.select().from(categories).where(eq(categories.id, id)).limit(1);
    return result[0];
  }

  async createCategory(categoryData: InsertCategory): Promise<Category> {
    const result = await db.insert(categories).values(categoryData).returning();
    return result[0];
  }

  // Product operations
  async getProducts(): Promise<Product[]> {
    return await db.select().from(products).where(eq(products.inStock, true));
  }

  async getProduct(id: number): Promise<Product | undefined> {
    const result = await db.select().from(products).where(eq(products.id, id)).limit(1);
    return result[0];
  }

  async getProductsByCategory(categoryId: number): Promise<Product[]> {
    return await db.select().from(products).where(
      and(eq(products.categoryId, categoryId), eq(products.inStock, true))
    );
  }

  async getFeaturedProducts(): Promise<Product[]> {
    return await db.select().from(products).where(
      and(eq(products.featured, true), eq(products.inStock, true))
    );
  }


  // Order operations
  async getUserOrders(userId: number): Promise<Order[]> {
    return await db.select().from(orders).where(eq(orders.userId, userId));
  }

  async getOrder(id: number): Promise<Order | undefined> {
    const result = await db.select().from(orders).where(eq(orders.id, id)).limit(1);
    return result[0];
  }

  async createOrder(orderData: InsertOrder): Promise<Order> {
    const result = await db.insert(orders).values(orderData).returning();
    return result[0];
  }

  async updateOrderStatus(id: number, status: string): Promise<Order | undefined> {
    const result = await db.update(orders)
      .set({ status })
      .where(eq(orders.id, id))
      .returning();

    return result[0];
  }

  // Product Management Methods
  async createProduct(productData: {
    name: string;
    description: string;
    price: number;
    salePrice?: number | null;
    categoryId: number;
    stockQuantity: number;
    featured: boolean;
    sale: boolean;
    image: string;
    unit: string;
  }): Promise<Product> {
    const result = await db.insert(products).values({
      name: productData.name,
      description: productData.description,
      price: productData.price.toString(),
      salePrice: productData.salePrice?.toString(),
      categoryId: productData.categoryId,
      unit: productData.unit,
      inStock: productData.stockQuantity > 0,
      featured: productData.featured,
      sale: productData.sale,
      image: productData.image
    }).returning();

    return result[0];
  }

  async updateProduct(id: number, productData: {
    name: string;
    description: string;
    price: number;
    salePrice?: number | null;
    categoryId: number;
    stockQuantity: number;
    featured: boolean;
    sale: boolean;
    image: string;
    unit: string;
  }): Promise<Product | undefined> {
    const result = await db.update(products)
      .set({
        name: productData.name,
        description: productData.description,
        price: productData.price.toString(),
        salePrice: productData.salePrice?.toString(),
        categoryId: productData.categoryId,
        unit: productData.unit,
        inStock: productData.stockQuantity > 0,
        featured: productData.featured,
        sale: productData.sale,
        image: productData.image,
        updatedAt: new Date()
      })
      .where(eq(products.id, id))
      .returning();

    return result[0];
  }

  async deleteProduct(id: number): Promise<boolean> {
    const result = await db.delete(products)
      .where(eq(products.id, id))
      .returning();

    return result.length > 0;
  }

  // Super Admin Methods
  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(users.createdAt);
  }

  async getUserById(id: number): Promise<User | null> {
    const result = await db.select().from(users).where(eq(users.id, id));
    return result[0] || null;
  }

  async updateUserRole(id: number, role: UserRole, permissions?: Permission[]): Promise<User | null> {
    const result = await db.update(users)
      .set({ 
        role, 
        permissions: permissions || null,
        updatedAt: new Date()
      })
      .where(eq(users.id, id))
      .returning();

    return result[0] || null;
  }

  async createAdminUser(userData: {
    username: string;
    email: string;
    password: string;
    firstName?: string;
    lastName?: string;
    role: UserRole;
    permissions?: Permission[];
  }): Promise<User> {
    const hashedPassword = await bcrypt.hash(userData.password, 10);

    const result = await db.insert(users)
      .values({
        username: userData.username,
        email: userData.email,
        password: hashedPassword,
        firstName: userData.firstName,
        lastName: userData.lastName,
        role: userData.role,
        permissions: userData.permissions || null,
        isActive: true
      })
      .returning();

    return result[0];
  }

  async deactivateUser(id: number): Promise<boolean> {
    const result = await db.update(users)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();

    return result.length > 0;
  }

  async activateUser(id: number): Promise<boolean> {
    const result = await db.update(users)
      .set({ isActive: true, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();

    return result.length > 0;
  }

  async deleteUser(id: number): Promise<boolean> {
    const result = await db.delete(users)
      .where(eq(users.id, id))
      .returning();

    return result.length > 0;
  }

  async logSystemAction(logData: {
    userId?: number;
    action: string;
    resource?: string;
    resourceId?: number;
    details?: any;
    ipAddress?: string;
    userAgent?: string;
  }): Promise<SystemLog> {
    const result = await db.insert(systemLogs)
      .values({
        userId: logData.userId,
        action: logData.action,
        resource: logData.resource,
        resourceId: logData.resourceId,
        details: logData.details,
        ipAddress: logData.ipAddress,
        userAgent: logData.userAgent
      })
      .returning();

    return result[0];
  }

  async getSystemLogs(limit: number = 100, offset: number = 0): Promise<SystemLog[]> {
    return await db.select()
      .from(systemLogs)
      .orderBy(systemLogs.createdAt)
      .limit(limit)
      .offset(offset);
  }

  async getSystemStats(): Promise<{
    totalUsers: number;
    activeUsers: number;
    totalProducts: number;
    totalOrders: number;
    totalCategories: number;
    recentLogins: number;
  }> {
    const [userStats, productStats, orderStats, categoryStats, recentLogins] = await Promise.all([
      db.select({ 
        id: users.id,
        isActive: users.isActive 
      }).from(users),
      db.select({ id: products.id }).from(products),
      db.select({ id: orders.id }).from(orders),
      db.select({ id: categories.id }).from(categories),
      db.select({ id: users.id }).from(users)
        .where(eq(users.lastLoginAt, new Date(Date.now() - 24 * 60 * 60 * 1000)))
    ]);

    return {
      totalUsers: userStats.length,
      activeUsers: userStats.filter(u => u.isActive).length,
      totalProducts: productStats.length,
      totalOrders: orderStats.length,
      totalCategories: categoryStats.length,
      recentLogins: recentLogins.length
    };
  }

  async updateUserLastLogin(id: number): Promise<void> {
    await db.update(users)
      .set({ lastLoginAt: new Date() })
      .where(eq(users.id, id));
  }

  async decrementProductStock(productId: number, quantity: number): Promise<void> {
    // First get the current stock quantity
    const product = await db.select({ stockQuantity: products.stockQuantity })
      .from(products)
      .where(eq(products.id, productId))
      .limit(1);

    if (product.length === 0) {
      throw new Error("Product not found");
    }

    const newStockQuantity = product[0].stockQuantity - quantity;

    // Update the product with new stock quantity
    await db.update(products)
      .set({ 
        stockQuantity: newStockQuantity,
        inStock: newStockQuantity > 0
      })
      .where(eq(products.id, productId));
  }
}

// Export the database storage instance
export const storage = new DatabaseStorage();

// Password verification utility
export const verifyPassword = async (password: string, hashedPassword: string): Promise<boolean> => {
  return await bcrypt.compare(password, hashedPassword);
};

