        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        credentials: "include",
        body: JSON.stringify(userData)
      });
      if (!response.ok) {
        throw new Error("Failed to create admin user");
      }
      return await response.json();
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const updateUserRole = async (id: number, role: string, permissions?: string[]): Promise<User> => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/super-admin/users/${id}/role`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        credentials: "include",
        body: JSON.stringify({ role, permissions })
      });
      if (!response.ok) {
        throw new Error("Failed to update user role");
      }
      return await response.json();
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const activateUser = async (id: number): Promise<boolean> => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/super-admin/users/${id}/activate`, {
        method: "PUT",
        credentials: "include"
      });
      if (!response.ok) {
        throw new Error("Failed to activate user");
      }
      return true;
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const deactivateUser = async (id: number): Promise<boolean> => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`/api/super-admin/users/${id}/deactivate`, {
        method: "PUT",
        credentials: "include"
      });
      if (!response.ok) {
        throw new Error("Failed to deactivate user");
      }
      return true;
    } catch (err) {
      setError(err instanceof Error ? err.message : "An error occurred");
      throw err;
    } finally {
      setLoading(false);
    }
