import { useState, useEffect, useCallback } from "react";
import { useTranslation } from "react-i18next";
import { Helmet } from "react-helmet-async";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Link, useLocation } from "wouter";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { useCart } from "@/hooks/use-cart";
import { useAuth } from "@/context/AuthContext";
import { userApi } from "@/hooks/use-api";
import { formatCurrency } from "@/lib/utils";
import { Address } from "@/types";
import { 
  ArrowLeft, 
  ArrowRight, 
  Check, 
  CreditCard, 
  Home,
  MapPin, 
  Timer, 
  Truck 
} from "lucide-react";

const formSchema = z.object({
  fullName: z.string().min(2, {
    message: "Full name must be at least 2 characters",
  }),
  email: z.string().email({
    message: "Please enter a valid email address",
  }),
  phone: z.string().min(10, {
    message: "Phone number must be at least 10 characters",
  }),
  address: z.string().min(5, {
    message: "Address must be at least 5 characters",
  }),
  city: z.string().min(2, {
    message: "City must be at least 2 characters",
  }),
  state: z.string().min(2, {
    message: "State must be at least 2 characters",
  }),
  zipCode: z.string().min(4, {
    message: "Zip code must be at least 4 characters",
  }),
  deliveryTime: z.string(),
  paymentMethod: z.enum(["uzcard", "humo", "click", "payme", "international", "cash"]),
  saveAddress: z.boolean().default(false),
  contactFree: z.boolean().default(false),
  notes: z.string().optional(),
});

type FormValues = z.infer<typeof formSchema>;

const CheckoutPage = () => {
  const { t } = useTranslation();
  const [, setLocation] = useLocation();
  const { cartItems, subtotal, deliveryFee, total, clearCart } = useCart();
  const { user } = useAuth();
  const [paymentStep, setPaymentStep] = useState<"address" | "delivery" | "payment" | "confirmation">("address");
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [savedAddresses, setSavedAddresses] = useState<Address[]>([]);
  const [selectedAddressId, setSelectedAddressId] = useState<number | null>(null);

  // Debug current step
  console.log("üõí CHECKOUT: Current step:", paymentStep);

  // Redirect to login if user is not authenticated
  useEffect(() => {
    if (!user) {
      setLocation("/auth");
    }
  }, [user, setLocation]);

  const loadSavedAddresses = useCallback(async () => {
    try {
      const data = await userApi.getAddresses();
      setSavedAddresses(data.addresses || []);

      // Set default address if available
      const defaultAddress = data.addresses?.find((addr: Address) => addr.isDefault);
      if (defaultAddress) {
        setSelectedAddressId(defaultAddress.id);
        fillFormWithAddress(defaultAddress);
      }
    } catch (error) {
      console.error("Failed to load addresses:", error);
    }
  }, []);

  // Load saved addresses when user is available
  useEffect(() => {
    if (user) {
      loadSavedAddresses();
    }
  }, [user, loadSavedAddresses]);

  const fillFormWithAddress = (address: Address) => {
    form.setValue("fullName", address.fullName);
    form.setValue("address", address.address);
    form.setValue("city", address.city);
    form.setValue("state", address.state);
    form.setValue("zipCode", address.postalCode);
  };

  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      fullName: user ? `${user.firstName || ""} ${user.lastName || ""}`.trim() : "",
      email: user?.email || "",
      phone: user?.phone || "",
      address: "",
      city: "",
      state: "",
      zipCode: "",
      deliveryTime: "asap",
      paymentMethod: "uzcard",
      saveAddress: false,
      contactFree: false,
      notes: "",
    },
  });

  // Auto-advance removed - user must manually progress through steps

  const onError = (errors: any) => {
    console.log("üõí CHECKOUT: Form validation errors:", errors);
    // Navigate to the first step with errors
    if (errors.fullName || errors.email || errors.phone || errors.address || errors.city || errors.state || errors.zipCode) {
      console.log("üõí CHECKOUT: Address errors found, navigating to address step");
      setPaymentStep("address");
    } else if (errors.deliveryTime) {
      console.log("üõí CHECKOUT: Delivery errors found, navigating to delivery step");
      setPaymentStep("delivery");
    }
  };

  const onSubmitActual = async (data: FormValues) => {
    console.log("üöÄ CHECKOUT: Form submitted!");
    console.log("üöÄ CHECKOUT: Form data:", data);

    try {
      // Save address if requested and create order
      let addressId = selectedAddressId;

      // If save address is checked or no address is selected, create a new one
      if (data.saveAddress || !addressId) {
        const addressResponse = await userApi.createAddress({
          title: "Home",
          fullName: data.fullName,
          address: data.address,
          city: data.city,
          state: data.state,
          postalCode: data.zipCode,
          country: "Uzbekistan",
          phone: data.phone,
          isDefault: savedAddresses.length === 0, // Make first address default
        });
        addressId = addressResponse.address.id;
      }

      // Prepare order items from cart
      const orderItems = cartItems.map(item => ({
        productId: item.id,
        quantity: item.quantity || 1,
        price: item.price,
        salePrice: item.salePrice,
        sale: item.sale || false,
      }));

      // Create the order
      const response = await fetch("/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          items: orderItems,
          addressId,
          paymentMethod: data.paymentMethod,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Failed to create order");
      }

      const result = await response.json();
      console.log("‚úÖ Order created:", result);

      // Show order confirmation
      setOrderPlaced(true);

      // Clear cart after successful order
      setTimeout(() => {
        clearCart();
        setLocation("/");
      }, 5000);
    } catch (error) {
      console.error("‚ùå Order creation failed:", error);
      alert("Failed to place order. Please try again.");
    }
  };

