}

// Product
export interface Product {
  id: number;
  name: string;
  nameEs: string;
  nameAr: string;
  description: string;
  descriptionEs: string;
  descriptionAr: string;
  price: number;
  image: string;
  category: Category;
  unit: string;
  unitEs: string;
  unitAr: string;
  inStock: boolean;
  featured?: boolean;
  sale?: boolean;
  salePrice?: number;
  nutrition?: {
    calories: number;
    fat: number;
    carbs: number;
    protein: number;
  };
  allergens?: string[];
}

