import { ShoppingCart } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useCart } from "@/hooks/use-cart";
import { cn } from "@/lib/utils";

export default function CartButton() {
  const { toggleCart, cartItems } = useCart();
  const itemCount = cartItems.reduce((total, item) => total + item.quantity, 0);

  const handleClick = () => {
    console.log("CartButton: Clicked! Current item count:", itemCount);
    toggleCart();
  };

  return (
    <Button
      variant="ghost"
      className="relative group p-2"
      aria-label="Shopping cart"
      onClick={handleClick}
    >
      <ShoppingCart className="h-5 w-5 text-neutral-700 group-hover:text-primary" />
      {itemCount > 0 && (
        <span className={cn(
          "absolute -top-2 -right-2 rtl:-right-auto rtl:-left-2",
          "bg-accent text-white text-xs font-bold rounded-full",
          "w-5 h-5 flex items-center justify-center"
        )}>
          {itemCount > 99 ? "99+" : itemCount}
        </span>
      )}
    </Button>
  );
}

client/src/components/ui/LanguageSelecto.content:
import React from "react";
import { useLanguage } from "@/hooks/use-language";
import { useTranslation } from "react-i18next";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { ChevronDown } from "lucide-react";

// Define our supported languages
const SUPPORTED_LANGUAGES = [
  { code: 'en', name: 'English', flag: 'us' },
  { code: 'ru', name: 'Russian', flag: 'ru' },
  { code: 'uz', name: 'Uzbek', flag: 'uz' }
];

interface LanguageSelectorProps {
  variant?: "header" | "mobile";
}

const LanguageSelector: React.FC<LanguageSelectorProps> = ({ 
  variant = "header" 
}) => {
  const { currentLanguage, setLanguage } = useLanguage();
  const { t } = useTranslation();

  const getFlagSrc = (code: string) => {
    const language = SUPPORTED_LANGUAGES.find(lang => lang.code === code);
    const flag = language ? language.flag : 'us';
    return `https://cdnjs.cloudflare.com/ajax/libs/flag-icon-css/3.5.0/flags/4x3/${flag}.svg`;
  };

  // Get current language name
  const currentLang = SUPPORTED_LANGUAGES.find(lang => lang.code === currentLanguage) || SUPPORTED_LANGUAGES[0];

  if (variant === "mobile") {
    return (
      <div className="flex flex-col space-y-2">
        <p className="font-medium text-neutral-800">Change Language</p>
        <div className="grid grid-cols-3 gap-2">
          {SUPPORTED_LANGUAGES.map(language => (
            <button 
              key={language.code}
              className={`flex items-center space-x-1 ${currentLanguage === language.code ? 'bg-primary text-white' : 'bg-neutral-100'} px-3 py-2 rounded hover:bg-primary/90 hover:text-white transition-colors`}
              onClick={() => setLanguage(language.code)}
            >
              <img src={getFlagSrc(language.code)} alt={language.name} className="w-4 h-4 rounded-sm" />
              <span className="ml-1">{language.name}</span>
            </button>
          ))}
        </div>
      </div>
    );
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger className="flex items-center space-x-1">
        <img src={getFlagSrc(currentLanguage)} alt={currentLang.name} className="w-4 h-4 rounded-sm" />
        <span className="ml-1">{currentLang.name}</span>
        <ChevronDown className="h-4 w-4" />
      </DropdownMenuTrigger>
      <DropdownMenuContent>
        {SUPPORTED_LANGUAGES.map(language => (
          <DropdownMenuItem 
            key={language.code}
            onClick={() => setLanguage(language.code)} 
            className="flex items-center space-x-2 p-2 hover:bg-neutral-100 rounded cursor-pointer"
          >
            <img src={getFlagSrc(language.code)} alt={language.name} className="w-4 h-4 rounded-sm" />
            <span className="ml-1">{language.name}</span>
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

export default LanguageSelector;

client/src/components/ui/ProductCard.tsx.content:
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Plus } from "lucide-react";
import { formatPrice } from "@/lib/currency";

interface Product {
  id: number;
  name: string;
  image: string;
  price: string;
  unit: string;
  categoryId: number;
  inStock: boolean;
  featured?: boolean;
  sale?: boolean;
  salePrice?: string;
}

interface ProductCardProps {
  product: Product;
  onAddToCart: () => void;
}

export default function ProductCard({ product, onAddToCart }: ProductCardProps) {
  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    console.log("Adding product to cart:", product.name);
    onAddToCart();
  };

  const displayPrice = product.sale && product.salePrice ? product.salePrice : product.price;
  const originalPrice = product.sale ? product.price : null;

  return (
    <div className="bg-white rounded-xl border border-neutral-200 overflow-hidden transition-all hover:shadow-md">
      <Link href={`/product/${product.id}`} className="block">
        <div className="relative">
          <img 
            src={product.image} 
            alt={product.name}
            className="w-full h-40 object-cover"
          />
          {product.featured && (
            <div className="absolute top-2 left-2 bg-primary text-white text-xs px-2 py-1 rounded">
              Featured
            </div>
          )}
          {product.sale && (
            <div className="absolute top-2 right-2 bg-red-500 text-white text-xs px-2 py-1 rounded">
              Sale
            </div>
          )}
        </div>
        <div className="p-4">
          <h3 className="font-medium">{product.name}</h3>
          <p className="text-sm text-neutral-500 mb-2">{product.unit}</p>
          <div className="flex items-center justify-between">
            <div className="flex flex-col">
              <span className="font-bold">{formatPrice(displayPrice)}</span>
              {originalPrice && (
                <span className="text-xs text-neutral-400 line-through">
                  {formatPrice(originalPrice)}
                </span>
              )}
            </div>
            <Button
              size="icon"
              className="h-8 w-8 rounded-full bg-primary text-white hover:bg-primary/90"
              onClick={handleAddToCart}
              disabled={!product.inStock}
            >
              <Plus className="h-4 w-4" />
            </Button>
          </div>
          {!product.inStock && (
            <div className="text-xs text-red-500 mt-1">Out of Stock</div>
          )}
        </div>
      </Link>
    </div>
  );
}

